mapper.update:
update product set 
product_code=#{productCode}, proposer_id=#{proposerId}, product_name=#{productName}, product_img=#{productImg}, product_url=#{productUrl}, product_qty=#{productQty}, product_price=#{productPrice}, 
requirement=#{requirement}, product_desc=#{productDesc}, requested_date=#{requestedDate}, deadline_date=#{deadlineDate}, depart_nation=#{departNation}, arrival_nation=#{arrivalNation}
where proposer_id='yd@yd' and product_code=#{productCode}

mapper.select:
select product_code, proposer_id, product_name, product_img, product_qty, product_price, product_desc, deadline_date,depart_nation, arrival_nation
from product
where proposer_id='yd@yd' and product_code=#{productCode}
